import React from "react";
import { BrowserRouter, Route, Switch, Redirect } from "react-router-dom";
import { isAuthenticated, getToken } from "./services/auth";

import routes from "./routes/index.js"

const PrivateRoute = ({ component: Component, ...rest }) => (
  <Route
    {...rest}
    render={props =>
      isAuthenticated() ? (
        <Component {...props} />
      ) : (
        <Redirect to={{ pathname: "/", state: { from: props.location } }} />
      )
    }
  />
);

const Padrao = ({ component: Component, params: params, ...rest}) => {
  teste = true;
  if(teste) {
  <Route
    {...rest}
    render={props =>
      !getToken() ? (
        <Component {...props} params={params}/>
      ) : (
        <Redirect to={{ pathname: "/app", state: { from: props.location } }} />
      )
    }
  />}else {
    console.log('faz isso')
  }
}


const All = ({ component: Component, params: params, ...rest}) => {

}


let data = {
  view: false,
}

const Routes = () => (
  <BrowserRouter>
    <Switch>
        {routes.map((item, i) => {
          return <All path={item.path} component={item.component} key={i} params={data} />
          }
        )}      
    </Switch>
  </BrowserRouter>
);

export default Routes;
