import React from "react";
import { BrowserRouter, Route, Switch, Redirect } from "react-router-dom";
import { isAuthenticated, isRegister } from "./services/auth";
import Login from "./views/login";
import Register from "./views/register";
const PrivateRoute = ({ component: Component, ...rest }) => (
  <Route
    {...rest}
    render={props =>
      isAuthenticated() ? (
        <Component {...props} />
      ) : (
        <Redirect to={{ patname: "/", state: { from: props.location } }} />
      )
    }
  />
);

/*const Padrao = ({ component: Component, ...rest }) => (
  <Route
    {...rest}
    render={props =>
      isRegister() ? (
        <Component {...props} />
      ) : (
        <Redirect to={{ patname: "/", state: { from: props.location } }} />
      )
    }
  />
);*/

const Routes = () => (
  <BrowserRouter>
    <Switch>
      <Route exact path="/" component={Login} />
      {/*<Padrao exact path="/register" component={Register} /> */}
      <PrivateRoute path="/home" component={() => <h1>Voce esta logado</h1>} />
    </Switch>
  </BrowserRouter>
);

export default Routes;
