const TOKEN_KEY = '@meutoken'

const isAuthenticated = () => localStorage.getItem(TOKEN_KEY);
const getToken = () => localStorage.getItem(TOKEN_KEY);
const isRegister = () => true;
export { isAuthenticated, isRegister };
